import React, { useState } from 'react';

function AdministrarGuias({ guias, setGuias }) {
  const [form, setForm] = useState({ nombre: '', descripcion: '', ruta: '' });

  const handleSubmit = (e) => {
    e.preventDefault();

    const nuevaGuia = {
      id: guias.length ? Math.max(...guias.map(g => g.id)) + 1 : 1,
      nombre: form.nombre,
      descripcion: form.descripcion,
      ruta: form.ruta,   // üëà NUEVO campo
      suspendida: false
    };

    setGuias([...guias, nuevaGuia]);
    setForm({ nombre: '', descripcion: '', ruta: '' });
  };

  const toggleSuspender = (id) => {
    setGuias(guias.map(guia =>
      guia.id === id ? { ...guia, suspendida: !guia.suspendida } : guia
    ));
  };

  const eliminarGuia = (id) => {
    setGuias(guias.filter(guia => guia.id !== id));
  };

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">Administrar Gu√≠as</h2>

      <form onSubmit={handleSubmit} className="mb-6">
        <div className="mb-2">
          <label className="block mb-1">Nombre:</label>
          <input
            type="text"
            value={form.nombre}
            onChange={e => setForm({ ...form, nombre: e.target.value })}
            className="border p-2 w-full"
            required
          />
        </div>

        <div className="mb-2">
          <label className="block mb-1">Descripci√≥n:</label>
          <input
            type="text"
            value={form.descripcion}
            onChange={e => setForm({ ...form, descripcion: e.target.value })}
            className="border p-2 w-full"
            required
          />
        </div>

        <div className="mb-2">
          <label className="block mb-1">Ruta del sistema:</label>
          <input
            type="text"
            value={form.ruta}
            onChange={e => setForm({ ...form, ruta: e.target.value })}
            placeholder="Ej: /mesadepartes"
            className="border p-2 w-full"
            required
          />
        </div>

        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded">
          Agregar Gu√≠a
        </button>
      </form>

      <table className="w-full border">
        <thead>
          <tr>
            <th className="border px-2 py-1">Nombre</th>
            <th className="border px-2 py-1">Descripci√≥n</th>
            <th className="border px-2 py-1">Ruta</th>
            <th className="border px-2 py-1">Acciones</th>
          </tr>
        </thead>
        <tbody>
          {guias.map(guia => (
            <tr key={guia.id} className={guia.suspendida ? 'bg-red-100' : ''}>
              <td className="border px-2 py-1">{guia.nombre}</td>
              <td className="border px-2 py-1">{guia.descripcion}</td>
              <td className="border px-2 py-1">{guia.ruta}</td>
              <td className="border px-2 py-1">
                <button
                  onClick={() => toggleSuspender(guia.id)}
                  className="bg-yellow-500 text-white px-2 py-1 rounded mr-2"
                >
                  {guia.suspendida ? 'Activar' : 'Suspender'}
                </button>
                <button
                  onClick={() => eliminarGuia(guia.id)}
                  className="bg-red-600 text-white px-2 py-1 rounded"
                >
                  Eliminar
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default AdministrarGuias;
