import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import MesaVirtual from './MesaVirtual';
import { faBookOpen } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

const GuiasRapidas = () => {
  const navigate = useNavigate();
  const areas = [
    {
      title: 'Mesa de Partes Virtual',
      desc: 'Accede a la guía de uso del Sistema de Mesa de Partes Virtual y conoce cómo registrar y consultar tus documentos ràpido.',
      component: 'mesa-virtual'
    },
    {
      title: 'Sistema de Gestión Documental',
      desc: 'Accede a la guía de uso del trámite Documentario y conoce más con la guía rápida sobre el sistema de manera sencilla.',
      component: 'gestion-documentaria'
    },
    {
      title: 'Sistema de Gestión de Contenidos',
      desc: 'Accede a la guía de uso del Sistema de Gestión de Contenidos y conoce más con la guía rápida de manera sencilla.',
      component: 'gestion-contenidos'
    },
    {
      title: 'Sistema de Mantenimiento',
      desc: 'Accede a la guía de uso del Sistema de Mantenimiento y conoce cómo registrar y consultar tus documentos de ràpido',
      component: 'mantenimiento'
    },
  ];

  const [selectedSystem, setSelectedSystem] = useState(null);

  const handleSystemSelect = (system) => {
    if (system === 'mesa-virtual') {
      navigate('/mesadepartes');
    } else if (system === 'gestion-documentaria') {
      navigate('/gestion-documentaria');
    } else if (system === 'gestion-contenidos') {
      navigate('/gestion-contenidos');
    } else if (system === 'mantenimiento') {
      navigate('/mantenimiento');
    } else {
      setSelectedSystem(system);
    }
  };

  const handleBack = () => {
    setSelectedSystem(null);
  };

  if (selectedSystem === 'mesa-virtual') {
    return <MesaVirtual onBack={handleBack} />;
  }


  if (selectedSystem) {
    return (
      <div className=" bg-white p-4" style={{ minHeight: '25vh' }}>
        <div className="max-w-16xl mx-auto mt-8">
          <h2 className="subtitulos">Sistema en Desarrollo</h2>
          <div className="text-center mt-8">
            <p className="text-gray-600 mb-4">
              Las guías para este sistema estarán disponibles próximamente.
            </p>
            <button
              onClick={handleBack}
              className="btn-primary"
            >
              <FontAwesomeIcon icon={faBookOpen} className="mr-2" />
              Volver a Guías Rápidas
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className='mt-12'>
      <h2 className="subtitulos">Guías Rápidas</h2>
      <div className="grid grid-cols-1 grid-cols-2 grid-cols-4 mt-2 p-2" style={{ minHeight: '22vh' }}> 
        {areas.map((area, index) => (
          <div
        key={index}
        className="card"
        onClick={() => handleSystemSelect(area.component)} 
        style={{ cursor: 'pointer' }}
      >
         <div className="btn-primary2">
        </div>
        <h3 className="mb-2 mt-2 text-center" style={{ fontSize: '17px', fontWeight: '600' }}>
          {area.title}
        </h3>
        <p className="text-gray-600 mb-4" style={{ padding: '0.8rem 2rem' }} > {area.desc}</p>
      </div>
        ))}
      </div>
    </div>
  );
};

export default GuiasRapidas;
