import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import MesaVirtual from './MesaVirtual';
import { faBookOpen } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

const GuiasRapidas = ({ guias }) => {
  const navigate = useNavigate();
  const [selectedSystem, setSelectedSystem] = useState(null);

  // Relacionar nombre con ruta
  const getComponentRoute = (nombre) => {
    if (nombre.toLowerCase().includes('mesa')) return 'mesa-virtual';
    if (nombre.toLowerCase().includes('documental')) return 'gestion-documentaria';
    if (nombre.toLowerCase().includes('contenidos')) return 'gestion-contenidos';
    if (nombre.toLowerCase().includes('mantenimiento')) return 'mantenimiento';
    return null;
  };

  const handleSystemSelect = (component) => {
    if (component === 'mesa-virtual') {
      navigate('/mesadepartes');
    } else if (component === 'gestion-documentaria') {
      navigate('/gestion-documentaria');
    } else if (component === 'gestion-contenidos') {
      navigate('/gestion-contenidos');
    } else if (component === 'mantenimiento') {
      navigate('/mantenimiento');
    } else {
      setSelectedSystem(component);
    }
  };

  const handleBack = () => {
    setSelectedSystem(null);
  };

  if (selectedSystem === 'mesa-virtual') {
    return <MesaVirtual onBack={handleBack} />;
  }

  if (selectedSystem) {
    return (
      <div className=" bg-white p-4" style={{ minHeight: '25vh' }}>
        <div className="max-w-16xl mx-auto mt-8">
          <h2 className="subtitulos">Sistema en Desarrollo</h2>
          <div className="text-center mt-8">
            <p className="text-gray-600 mb-4">
              Las guías para este sistema estarán disponibles próximamente.
            </p>
            <button
              onClick={handleBack}
              className="btn-primary"
            >
              <FontAwesomeIcon icon={faBookOpen} className="mr-2" />
              Volver a Guías Rápidas
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className='mt-12'>
      <h2 className="subtitulos">Guías Rápidas</h2>
      <div className="grid grid-cols-1 grid-cols-2 grid-cols-4 mt-2 p-2" style={{ minHeight: '22vh' }}>
        {guias.filter(g => !g.suspendida).map((guia, index) => {
          const component = getComponentRoute(guia.nombre);
          return (
            <div
              key={guia.id}
              className="card"
              onClick={() => handleSystemSelect(component)}
              style={{ cursor: 'pointer' }}
            >
              <div className="btn-primary2"></div>
              <h3 className="mb-2 mt-2 text-center" style={{ fontSize: '17px', fontWeight: '600' }}>
                {guia.nombre}
              </h3>
              <p className="text-gray-600 mb-4" style={{ padding: '0.8rem 2rem' }}>{guia.descripcion}</p>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default GuiasRapidas;
