import React, { useState } from 'react';

const initialGuias = [
	{
		id: 1,
		nombre: 'Mesa de Partes Virtual',
		descripcion: 'Accede a la guía de uso del Sistema de Mesa de Partes Virtual y conoce cómo registrar y consultar tus documentos rápido.',
		suspendida: false
	},
	{
		id: 2,
		nombre: 'Sistema de Gestión Documental',
		descripcion: 'Accede a la guía de uso del trámite Documentario y conoce más con la guía rápida sobre el sistema de manera sencilla.',
		suspendida: false
	},
	{
		id: 3,
		nombre: 'Sistema de Gestión de Contenidos',
		descripcion: 'Accede a la guía de uso del Sistema de Gestión de Contenidos y conoce más con la guía rápida de manera sencilla.',
		suspendida: false
	},
	{
		id: 4,
		nombre: 'Sistema de Mantenimiento',
		descripcion: 'Accede a la guía de uso del Sistema de Mantenimiento y conoce cómo registrar y consultar tus documentos rápido.',
		suspendida: false
	},
];

const AdministrarGuias = () => {
	const [guias, setGuias] = useState(initialGuias);
	const [form, setForm] = useState({ nombre: '', descripcion: '' });
	const [editId, setEditId] = useState(null);
	const [editForm, setEditForm] = useState({ nombre: '', descripcion: '' });

	// Agregar nueva guía
	const handleAddGuia = (e) => {
		e.preventDefault();
		if (!form.nombre.trim() || !form.descripcion.trim()) return;
		setGuias([
			...guias,
			{
				id: guias.length ? Math.max(...guias.map(g => g.id)) + 1 : 1,
				nombre: form.nombre,
				descripcion: form.descripcion,
				suspendida: false
			}
		]);
		setForm({ nombre: '', descripcion: '' });
	};

	// Eliminar guía
	const handleDelete = (id) => {
		setGuias(guias.filter(g => g.id !== id));
	};

	// Suspender guía
	const handleSuspend = (id) => {
		setGuias(guias.map(g => g.id === id ? { ...g, suspendida: !g.suspendida } : g));
	};

	// Editar guía
	const handleEdit = (guia) => {
		setEditId(guia.id);
		setEditForm({ nombre: guia.nombre, descripcion: guia.descripcion });
	};

	const handleEditSave = (id) => {
		setGuias(guias.map(g => g.id === id ? { ...g, nombre: editForm.nombre, descripcion: editForm.descripcion } : g));
		setEditId(null);
		setEditForm({ nombre: '', descripcion: '' });
	};

	return (
		<div className="p-6">
			<h2 className="subtitulos mb-4">Administrar Guías Rápidas</h2>
			{/* Formulario para agregar nueva guía */}
			<form onSubmit={handleAddGuia} className="mb-6 bg-gray-100 p-4 rounded">
				<h3 className="mb-2">Agregar Nueva Guía</h3>
				<div className="mb-2">
					<label className="block mb-1">Nombre del sistema:</label>
					<input
						type="text"
						value={form.nombre}
						onChange={e => setForm({ ...form, nombre: e.target.value })}
						className="border p-2 w-full"
						required
					/>
				</div>
				<div className="mb-2">
					<label className="block mb-1">Descripción:</label>
					<textarea
						value={form.descripcion}
						onChange={e => setForm({ ...form, descripcion: e.target.value })}
						className="border p-2 w-full"
						required
					/>
				</div>
				<button type="submit" className="btn-primary">Agregar</button>
			</form>

			{/* Tabla de guías */}
			<table className="min-w-full bg-white border">
				<thead>
					<tr>
						<th className="border px-4 py-2">Ítem</th>
						<th className="border px-4 py-2">Nombre Guía</th>
						<th className="border px-4 py-2">Descripción</th>
						<th className="border px-4 py-2">Editar</th>
						<th className="border px-4 py-2">Eliminar</th>
						<th className="border px-4 py-2">Suspender</th>
					</tr>
				</thead>
				<tbody>
					{guias.map((guia, idx) => (
						<tr key={guia.id} className={guia.suspendida ? 'bg-red-100' : ''}>
							<td className="border px-4 py-2">{idx + 1}</td>
							<td className="border px-4 py-2">
								{editId === guia.id ? (
									<input
										type="text"
										value={editForm.nombre}
										onChange={e => setEditForm({ ...editForm, nombre: e.target.value })}
										className="border p-1 w-full"
									/>
								) : (
									guia.nombre
								)}
							</td>
							<td className="border px-4 py-2">
								{editId === guia.id ? (
									<textarea
										value={editForm.descripcion}
										onChange={e => setEditForm({ ...editForm, descripcion: e.target.value })}
										className="border p-1 w-full"
									/>
								) : (
									guia.descripcion
								)}
							</td>
							<td className="border px-4 py-2">
								{editId === guia.id ? (
									<button className="btn-primary" onClick={() => handleEditSave(guia.id)}>Guardar</button>
								) : (
									<button className="btn-primary" onClick={() => handleEdit(guia)}>Editar</button>
								)}
							</td>
							<td className="border px-4 py-2">
								<button className="btn-danger" onClick={() => handleDelete(guia.id)}>Eliminar</button>
							</td>
							<td className="border px-4 py-2">
								<button className="btn-warning" onClick={() => handleSuspend(guia.id)}>
									{guia.suspendida ? 'Activar' : 'Suspender'}
								</button>
							</td>
						</tr>
					))}
				</tbody>
			</table>
		</div>
	);
};

export default AdministrarGuias;
