import React from 'react';
import './App.css';
import GuiasRapidas from './components/GuiasRapidas';

function App() {
  const fechaActualizada = new Date().toLocaleDateString('es-PE', {
    year: 'numeric',
    month: 'long',
    day: 'numeric'
  });

  return (
    <div className="App">
      {/* Encabezado Fijo */}
      <div
        style={{
          left: 0,
          position: 'fixed',
          right: 0,
          top: 0,
          zIndex: 1037,
          height: '3rem',
          background: '#99020B',
          display: 'flex',
          alignItems: 'center',
          padding: '0 1rem',
          justifyContent: 'flex-start',
        }}
      >
        <div style={{ display: 'flex', alignItems: 'center'}}>
          <img
            src="./img/uni_blanco.ico"
            style={{ width: '2.2rem', marginLeft: '1rem' }}
            alt="logouni"
          />
          <img
            src="./img/slasch.ico"
            style={{ width: '2.2rem', marginLeft: '0rem' }}
            alt="logouni"
          />
          <div
            style={{
              marginLeft: '0rem',
              verticalAlign: 'middle',
              fontFamily: 'Segoe UI'
            }}
          >
            <span style={{ 
              color: 'white', 
              fontFamily: 'Segoe UI', 
              fontSize: '12px', 
              fontWeight: 'bold'
            }}>
              UNI - GUÍAS RÁPIDAS - {fechaActualizada}
            </span>
          </div>
        </div>
      </div>
      
      {/* Contenido principal con margen superior */}
      <div style={{ marginTop: '4rem' }}>
        <GuiasRapidas />
      </div>
    </div>
  );
}

export default App;
