import React from 'react';
import { useNavigate } from 'react-router-dom';

function GuiasRapidas({ guias }) {
  const navigate = useNavigate();

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">Guías Rápidas</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
        {guias.filter(g => !g.suspendida).map(guia => (
          <div
            key={guia.id}
            className="card border p-4 shadow hover:shadow-lg rounded cursor-pointer"
            onClick={() => guia.ruta ? navigate(guia.ruta) : alert('Sistema en desarrollo')}
          >
            <h3 className="text-lg font-semibold mb-2">{guia.nombre}</h3>
            <p className="text-sm text-gray-600">{guia.descripcion}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default GuiasRapidas;
