import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const AdministrarTutos = ({ guias }) => {
  const { id } = useParams();
  const navigate = useNavigate();
  const guia = guias?.find(g => String(g.id) === String(id));
  const [tutos, setTutos] = useState([]);
  const [form, setForm] = useState({ nombre: '', descripcion: '', video: '', doc: '' });
  const [editId, setEditId] = useState(null);

  // Cargar tutoriales desde localStorage al montar
  useEffect(() => {
    if (!guia) return;
    const key = `tutos_${guia.id}`;
    const stored = localStorage.getItem(key);
    if (stored) {
      setTutos(JSON.parse(stored));
    } else {
      // Si es la guía de Mesa de Partes, inicializa los tutoriales por defecto
      if (guia.ruta === '/mesadepartes' || guia.nombre.toLowerCase().includes('mesa')) {
        const defaultTutos = [
          {
            id: 1,
            nombre: 'Crear Cuenta',
            descripcion: 'Regístrese para acceder al sistema',
            video: './assets/video/CREAR_CUENTA.mp4',
            doc: './assets/ManualMesaPartes.pdf#page=4'
          },
          {
            id: 2,
            nombre: 'Registrar Expediente',
            descripcion: 'Ingrese un nuevo expediente al sistema',
            video: './assets/video/REGISTRAR_EXPEDIENTE.mp4',
            doc: './assets/ManualMesaPartes.pdf#page=7'
          },
          {
            id: 3,
            nombre: 'Consultar Expediente',
            descripcion: 'Consulte sus expedientes registrados',
            video: './assets/video/CONSULTAR_EXPEDIENTE.mp4',
            doc: './assets/ManualMesaPartes.pdf#page=15'
          }
        ];
        setTutos(defaultTutos);
        localStorage.setItem(key, JSON.stringify(defaultTutos));
      }
    }
  }, [guia]);

  // Guardar tutoriales en localStorage cuando cambian
  useEffect(() => {
    if (!guia) return;
    const key = `tutos_${guia.id}`;
    localStorage.setItem(key, JSON.stringify(tutos));
  }, [tutos, guia]);

  // Agregar o actualizar tutorial
  const handleAddTuto = (e) => {
    e.preventDefault();
    if (!form.nombre.trim() || !form.descripcion.trim() || !form.video.trim() || !form.doc.trim()) return;
    if (editId) {
      setTutos(tutos.map(t => t.id === editId ? { ...t, ...form } : t));
      setEditId(null);
    } else {
      setTutos([
        ...tutos,
        {
          id: tutos.length ? Math.max(...tutos.map(t => t.id)) + 1 : 1,
          nombre: form.nombre,
          descripcion: form.descripcion,
          video: form.video,
          doc: form.doc
        }
      ]);
    }
    setForm({ nombre: '', descripcion: '', video: '', doc: '' });
  };

  // Eliminar tutorial
  const handleDelete = (id) => {
    setTutos(tutos.filter(t => t.id !== id));
  };

  // Editar tutorial
  const handleEdit = (tuto) => {
    setEditId(tuto.id);
    setForm({ nombre: tuto.nombre, descripcion: tuto.descripcion, video: tuto.video, doc: tuto.doc });
  };

  if (!guia) {
    return (
      <div className="p-6">
        <h2 className="subtitulos mt-6">Guía no encontrada</h2>
        <button className="btn-primary mb-4" onClick={() => navigate('/administrar')}>Volver a Guías</button>
      </div>
    );
  }

  return (
    <div className="p-6">
      <h2 className="subtitulos mt-6">Tutoriales de {guia.nombre}</h2>
      <button className="btn-primary mb-4" onClick={() => navigate('/administrar')}>Volver a Guías</button>
      <form onSubmit={handleAddTuto} className="mb-6 p-4 rounded" style={{ maxWidth: "800px", width: "100%", margin: "0 auto" }}>
        <h3 className="subtitulos mb-4 mt-2" style={{ width: "95%" }}>
          {editId ? `Editar Tutorial ${form.nombre}` : "Agregar Nuevo Tutorial"}
        </h3>
        <div className="mb-2 flex items-center">
          <label className="ancho1" style={{ marginRight: "6px" }}>Nombre del tutorial:</label>
          <input type="text" value={form.nombre} onChange={e => setForm({ ...form, nombre: e.target.value })} className="border ancho2" required />
          <label className="ancho1" style={{ marginLeft: "15px" }}>Nombre de video:</label>
          <input type="text" value={form.video} onChange={e => setForm({ ...form, video: e.target.value })} className="border ancho2" required />
        </div>
        <div className="mb-2 flex">
          <label className="ancho1" style={{ marginRight: "50px" }}>Descripción:</label>
          <textarea value={form.descripcion} onChange={e => setForm({ ...form, descripcion: e.target.value })} className="border ancho3" required />
        </div>
        <div className="mb-2 flex">
          <label className="ancho1" style={{ marginRight: "50px" }}>Documentación:</label>
          <input type="text" value={form.doc} onChange={e => setForm({ ...form, doc: e.target.value })} className="border ancho3" required />
        </div>
        <button type="submit" className="btn-primary">
          {editId ? "Guardar Cambios" : "Agregar"}
        </button>
      </form>
      <table className="custom-table">
        <thead>
          <tr>
            <th>Ítem</th>
            <th>Nombre Tutorial</th>
            <th>Descripción</th>
            <th>Video</th>
            <th>Documentación</th>
            <th>Editar</th>
            <th>Eliminar</th>
          </tr>
        </thead>
        <tbody>
          {tutos.map((tuto, idx) => (
            <tr key={tuto.id}>
              <td>{idx + 1}</td>
              <td>{tuto.nombre}</td>
              <td>{tuto.descripcion}</td>
              <td>{tuto.video}</td>
              <td>{tuto.doc}</td>
              <td>
                <button className="btn-primary" onClick={() => handleEdit(tuto)}>Editar</button>
              </td>
              <td>
                <button className="btn-primary" onClick={() => handleDelete(tuto.id)}>Eliminar</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdministrarTutos;
