import React, { useState } from 'react';

const AdministrarGuias = ({ guias, setGuias }) => {
  const [form, setForm] = useState({ nombre: '', descripcion: '', ruta: '' });
  const [editId, setEditId] = useState(null);
  const [editForm, setEditForm] = useState({ nombre: '', descripcion: '', ruta: '' });

  // Agregar nueva guía
  const handleAddGuia = (e) => {
    e.preventDefault();
    if (!form.nombre.trim() || !form.descripcion.trim() || !form.ruta.trim()) return;
    setGuias([
      ...guias,
      {
        id: guias.length ? Math.max(...guias.map(g => g.id)) + 1 : 1,
        nombre: form.nombre,
        descripcion: form.descripcion,
        ruta: form.ruta,
        suspendida: false
      }
    ]);
    setForm({ nombre: '', descripcion: '', ruta: '' });
  };

  // Eliminar guía
  const handleDelete = (id) => {
    setGuias(guias.filter(g => g.id !== id));
  };

  // Suspender guía
  const handleSuspend = (id) => {
    setGuias(guias.map(g => g.id === id ? { ...g, suspendida: !g.suspendida } : g));
  };

  // Editar guía
  const handleEdit = (guia) => {
    setEditId(guia.id);
    setEditForm({ nombre: guia.nombre, descripcion: guia.descripcion, ruta: guia.ruta });
  };

  const handleEditSave = (id) => {
    setGuias(guias.map(g => g.id === id ? { ...g, ...editForm } : g));
    setEditId(null);
    setEditForm({ nombre: '', descripcion: '', ruta: '' });
  };

  return (
    <div className="p-6 ">
      <h2 className="subtitulos mt-6">Administrar Guías Rápidas</h2>      
      <form onSubmit={handleAddGuia} 
        className="mb-6 p-4 rounded"
        style={{ maxWidth: "1500px", width: "100%", marginLeft: "300px"}}
      >
        <h3 className="subtitulos mb-4 mt-2" style={{ width: "62%" }}>Agregar Nueva Guía</h3>
        <div className="mb-2 flex items-center">
            <label className="ancho1">Nombre del sistema:</label>
            <input
                type="text"
                value={form.nombre}
                onChange={e => setForm({ ...form, nombre: e.target.value })}
                className="border ancho2"
                required
            />
            <label className="ancho1" style={{ marginLeft: "15px" }}>Ruta del sistema:</label>
                <input
                    type="text"
                    value={form.ruta}
                    onChange={e => setForm({ ...form, ruta: e.target.value })}
                    placeholder="Ej: /mesadepartes"
                    className="border ancho2"
                    required
                />
        </div>

        <div className="mb-2 flex">
            <label className="ancho1" style={{ marginRight: "50px" }}>Descripción:</label>
            <textarea
                value={form.descripcion}
                onChange={e => setForm({ ...form, descripcion: e.target.value })}
                className="border ancho3"
                required
            />
        </div>
        <button type="submit" className="btn-primary" style={{ marginRight: "480px" }} >Agregar</button>
      </form>

      <table className="custom-table">
        <thead>
            <tr>
            <th>Ítem</th>
            <th>Nombre Guía</th>
            <th>Descripción</th>
            <th>Ruta</th>
            <th>Editar</th>
            <th>Eliminar</th>
            <th>Suspender</th>
            </tr>
        </thead>
        <tbody>
            {guias.map((guia, idx) => (
            <tr key={guia.id} className={guia.suspendida ? 'bg-red-100' : ''}>
                <td>{idx + 1}</td>
                <td>
                {editId === guia.id ? (
                    <input
                    type="text"
                    value={editForm.nombre}
                    onChange={e => setEditForm({ ...editForm, nombre: e.target.value })}
                    className="border p-1 w-full"
                    />
                ) : (
                    guia.nombre
                )}
                </td>
                <td>
                {editId === guia.id ? (
                    <textarea
                    value={editForm.descripcion}
                    onChange={e => setEditForm({ ...editForm, descripcion: e.target.value })}
                    className="border p-1 w-full"
                    />
                ) : (
                    guia.descripcion
                )}
                </td>
                <td>
                {editId === guia.id ? (
                    <input
                    type="text"
                    value={editForm.ruta}
                    onChange={e => setEditForm({ ...editForm, ruta: e.target.value })}
                    className="border p-1 w-full"
                    />
                ) : (
                    guia.ruta
                )}
                </td>
                <td>
                {editId === guia.id ? (
                    <button className="btn-primary" onClick={() => handleEditSave(guia.id)}>Guardar</button>
                ) : (
                    <button className="btn-primary" onClick={() => handleEdit(guia)}>Editar</button>
                )}
                </td>
                <td>
                <button className="btn-primary" onClick={() => handleDelete(guia.id)}>Eliminar</button>
                </td>
                <td>
                <button className="btn-primary" onClick={() => handleSuspend(guia.id)}>
                    {guia.suspendida ? 'Activar' : 'Suspender'}
                </button>
                </td>
            </tr>
            ))}
        </tbody>
        </table>
    </div>
  );
};

export default AdministrarGuias;
