import React, { useState } from 'react';
import MesaVirtual from './MesaVirtual';
import { faBookOpen } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

const GuiasRapidas = () => {
  const areas = [
    {
      title: 'Mesa de Partes Virtual',
      desc: 'Accede a la guía de uso del Sistema de Mesa de Partes Virtual y conoce cómo registrar y consultar tus documentos de manera sencilla.',
      component: 'mesa-virtual'
    },
    {
      title: 'Sistema de Gestión Documental',
      desc: 'Accede a la guía de uso del trámite Documentario y conoce más con la guía rápida sobre el sistema de manera sencilla.',
      component: 'gestion-documentaria'
    },
    {
      title: 'Sistema de Gestión de Contenidos',
      desc: 'Accede a la guía de uso del Sistema de Gestión de Contenidos y conoce más con la guía rápida de manera sencilla.',
      component: 'gestion-contenidos'
    },
    {
      title: 'Sistema de Mantenimiento',
      desc: 'Accede a la guía de uso del Sistema de Mantenimiento y conoce cómo registrar y consultar tus documentos de manera sencilla.',
      component: 'mantenimiento'
    },
  ];

  const [selectedSystem, setSelectedSystem] = useState(null);

  const handleSystemSelect = (system) => {
    setSelectedSystem(system);
  };

  const handleBack = () => {
    setSelectedSystem(null);
  };

  if (selectedSystem === 'mesa-virtual') {
    return <MesaVirtual onBack={handleBack} />;
  }


  if (selectedSystem) {
    return (
      <div className="min-h-screen bg-white p-4">
        <div className="max-w-12xl mx-auto mt-8">
          <h2 className="subtitulos">Sistema en Desarrollo</h2>
          <div className="text-center mt-8">
            <p className="text-gray-600 mb-4">
              Las guías para este sistema estarán disponibles próximamente.
            </p>
            <button
              onClick={handleBack}
              className="btn-primary"
            >
              <FontAwesomeIcon icon={faBookOpen} className="mr-2" />
              Volver a Guías Rápidas
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className='mt-12'>
  <h2 className="subtitulos">Guías Rápidas</h2>
  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 p-2" style={{ minHeight: '25vh' }}> 
    {areas.map((area, index) => (
      <div key={index} className="bg-white p-4 rounded-lg shadow-md hover:shadow-lg transition-shadow">
        <h3 className="text-lg font-semibold mb-2 text-center">{area.title}</h3>
        <p className="text-gray-600 mb-4 p-2">{area.desc}</p>
        <button
          onClick={() => setShowMesaVirtual(true)}
          style={{ background: '#99020b', color: 'white', padding: '5px 12px 5px 9px', fontSize: '12px', borderRadius: '5px' }}
        >
          <FontAwesomeIcon icon={faBookOpen} className="mr-3" />
          Ver Guía
        </button>
      </div>
    ))}
  </div>
</div>
  );
};

export default GuiasRapidas;
